var React = require('react');

module.exports = React.createClass({
    displayName: 'ProgressBar',
    propTypes: {
        value: React.PropTypes.number,
        max: React.PropTypes.number
    },

    render: function() {
        // Would use an HTML5 progress tag but if that doesn't animate if you
