            return (
                <button className="mx_EnableNotificationsButton" onClick={this.onClick}>
                    Disable Notifications
                </button>
            );
        } else {
            return (
                <button className="mx_EnableNotificationsButton" onClick={this.onClick}>
                    Enable Notifications
                </button>
            );
        }
    }
});

