        getPendingEvents: () => [],
        getLiveTimeline: () => stubTimeline,
        getUnfilteredTimelineSet: () => null,
        getAccountData: () => null,
        hasMembershipState: () => null,
        currentState: {
            getStateEvents: sinon.stub(),
            members: [],
        },
    };
}

